int  main(void)
{
  uint8_t temp;
 
    // Enable the GPIO port that is used for the on-board LED.
   SysCtlPeripheralEnable(SYSCTL_PERIPH_GPIOF);
   SetupHardware();
  
  // Check if the peripheral access is enabled.
    while(!SysCtlPeripheralReady(SYSCTL_PERIPH_GPIOF))
    {
    
  }
    
  // Enable the GPIO pin for the LED (PF3).  Set the direction as output, and
    // enable the GPIO pin for digital function.
    GPIOPinTypeGPIOOutput(GPIO_PORTF_BASE, GPIO_PIN_3);
  
  //loop forever
    while(1)
    {
   
    UARTCharPut(UART0_BASE, temp);  //send a character
     temp++;              //next character
  
   
     printf(" Select An Option:  \n");
   
    printf(" Print a Variable %c\n\r", temp);
   
    printf("1: This is the first case \n");
   
    printf("2: This is the 2nd case \n");
   
    printf("3: This is the 3rd case \n");
   
    printf("4: This is the 4th case \n");
   
   
   
     temp = getc(stdin);
   
   switch(temp)
   {
    case '1' : donkey();
    break;
    case '2' : astrohomer();
    break;
    case '3' : stormtrooper();
    break;
    case '4' :  wolverine();
  
   }
   
    // Turn on the LED.
        GPIOPinWrite(GPIO_PORTF_BASE, GPIO_PIN_3, 0xF);


        // Delay for a bit.
        for(int ui32Loop = 0; ui32Loop < 200000; ui32Loop++)
        {
        }


        // Turn off the LED.
        GPIOPinWrite(GPIO_PORTF_BASE, GPIO_PIN_3, 0x0);


        // Delay for a bit.
        for(int ui32Loop = 0; ui32Loop < 200000; ui32Loop++)
        {
        }
    }
 }